import 'package:bloc/bloc.dart';import 'package:flutter/material.dart';import '../../data/model/hive_model/product_holder.dart';import '../../data/source/local/my_hive_helper.dart';part 'favourite_event.dart';part 'favourite_state.dart';class FavouriteBloc extends Bloc<FavouriteEvent, FavouriteState> {  FavouriteBloc() : super(FavouriteInitial()) {    on<GetAllFavouriteProductsEvent>((event, emit) async {      final data = HiveHelper.getAllFavouriteProducts();      emit(GetAllFavouriteProductsState(data));    });    on<DeleteProductToFavouriteEvent>((event, emit) {      final data = HiveHelper.getAllFavouriteProducts();      HiveHelper.deleteProductFromFavourite(event.data);      emit(GetAllFavouriteProductsState(data));    });  }}