import 'package:flutter/cupertino.dart';import '../../data/source/local/my_hive_helper.dart';class DashboardProvider extends ChangeNotifier {  int count = 0;  DashboardProvider() {    getBasketCount();  }  Future<void> getBasketCount() async {    count = HiveHelper.getAllProductsFromBasket().length;    notifyListeners();  }  void increment() {    count++;    notifyListeners();  }  void decrement() {    count--;    notifyListeners();  }}